import React, { Component } from 'react';
import { View, Text, Image ,Alert ,ScrollView, ActivityIndicator, AsyncStorage } from 'react-native';
import Header from './Header';
import  axios  from 'axios';
import { Avatar, Button, Card, IconButton,Title, Paragraph ,Subheading } from 'react-native-paper';
import { StyleSheet } from 'react-native';
// import { white } from 'react-native-paper/lib/typescript/src/styles/colors';
import Loading from './loading';


export default class CurrentWeather extends Component {
  constructor(props) {
    super(props);
    this.state = {
      weatherobject : '',
      Country_id : null,
      feels_like : null,
      temp : null,
      temp_max : null,
      temp_min : null,
      pressure : null,
      humidity : null,
      icon : null,
      wind_degree : null,
      wind_speed : null,
      lat : null,
      long:null,
      desc : null,
      like_count : null,
      status:'loading',
    };
  }
    getwheather= async (city) =>{
       let wheather = null
        await axios.get(`http://api.openweathermap.org/data/2.5/weather?q=Mumbai&units=metric&appid=55c190d651c95a6c7716e0dc67ed1322`).then(
            function (response) {
                wheather = response.data
                console.log(response.data);
                
            }
           
        ).catch(err => {
            Alert.alert('Error','Oops!! There occured an error while procssing your request!!Check your Internet Connection Or Please try again later.\nError : '+err.message+'')
            console.log(err.message)
        })
        this.setState({
            weatherobject : wheather,
            Country_id : `City id : ${wheather.id}`,
            feels_like : wheather.main.feels_like,
            temp : wheather.main.temp,
            temp_max : wheather.main.temp_max,
            temp_min : wheather.main.temp_min,
            pressure : wheather.main.pressure,
            humidity : wheather.main.humidity,
            icon : (wheather.weather[0]).icon,
            wind_degree : wheather.wind.deg,
            wind_speed : wheather.wind.speed,
            lat : wheather.coord.lat,
            long:wheather.coord.lon,
            desc : (wheather.weather[0]).description,
            like_count : 0,
            status : 'loaded',
            like : 0
        })
        // console.log((wheather.weather[0]).icon)
        // await axios.get()
        // console.log(this.state.weatherobject);
    }

    componentDidMount = () => {
      AsyncStorage.clear()
        this.getwheather('dante')
    }
  render() {
    var mylike = null
    if(this.state.like === 0 )
    {
      mylike = (
        <Card  style = {{borderColor:'black',borderRadius : 10,margin:4,borderBottomWidth:2}}>
      <Card.Content>
        <Subheading>
          Did you like this , show us some support by hitting the like button.
        </Subheading>
        <IconButton
    icon="thumb-up-outline"
    color='red'
    size={20}
    onPress={() => this.setState({
      like_count : this.state.like_count+1
    })}
  />
      </Card.Content>
         
      </Card>
      )
    }
    else{
      mylike = (
        <Card  style = {{borderColor:'black',borderRadius : 10,margin:4,borderBottomWidth:2}}>
      <Card.Content>
        <Subheading>
        Thank You for showing immense love to us.
        Have a nice day!
        </Subheading>
        <IconButton
    icon="heart"
    color='red'
    size={20}
    onPress={() => this.setState({
      like_count : this.state.like_count+1
    })}
  />
      </Card.Content>
         
      </Card>
      )
    }
    if(this.state.status === 'loaded'){
    return (
        <View style = {{flex :1}}>        
        <Header title = 'Current Weather'/>
        <ScrollView style={{flex:1}}>
        <Card style = {styles.cardView} >
        <Card.Title style = {{ alignContent :'center'}} title={this.state.weatherobject.name} subtitle={this.state.Country_id} left={(props) => <Avatar.Icon {...props} icon="city-variant-outline" />} />
        <Card.Content style = {{ alignContent :'center'}}>
        <Card style = {{borderColor:'black',borderRadius : 10,borderBottomWidth:2 , margin:4}}>
        <Card.Content style = {{ alignContent :'center'}}>
      <Subheading>Temperature Stats of {this.state.weatherobject.name}:</Subheading>
      <Paragraph>Feels Like : {this.state.feels_like}{'\u00b0'}C </Paragraph>
      <Paragraph>Avergae Temperature : {this.state.temp}{'\u00b0'}C</Paragraph>
      <Paragraph>Maximum Temperature : {this.state.temp_max}{'\u00b0'}C</Paragraph>
      <Paragraph>Minimum Temperature : {this.state.temp_min}{'\u00b0'}C</Paragraph>
      </Card.Content>
      </Card>
      <Card style = {{borderColor:'black',borderRadius : 10,margin:4,borderBottomWidth:2}}>
        <Card.Content style = {{ alignContent :'center'}}>
        <Subheading>Humidity in {this.state.weatherobject.name}:</Subheading>
      <Paragraph>Humidity : {this.state.humidity}g/m^3</Paragraph>
      </Card.Content>
      </Card>
      <Card style = {{borderColor:'black',borderRadius : 10,margin:4,borderBottomWidth:2}}>
        <Card.Content style = {{ alignContent :'center'}}>
        <Subheading>Currnet climate conditions in {this.state.weatherobject.name}:</Subheading>
      <Paragraph>Climate conditons : {this.state.desc}</Paragraph>
      <Image style = {{height:120,width:120}} source={{ uri: 'http://openweathermap.org/img/w/'+this.state.icon+".png" }} />
      </Card.Content>
      </Card>
      <Card style = {{borderColor:'black',borderRadius : 10,margin:4,borderBottomWidth:2}}>
        <Card.Content style = {{ alignContent :'center'}}>
        <Subheading>Pressure in {this.state.weatherobject.name}:</Subheading>
        <Paragraph> Pressure : {this.state.pressure}N/m^2</Paragraph>

      </Card.Content>
      
      </Card>
      <Card style = {{borderColor:'black',borderRadius : 10,margin:4,borderBottomWidth:2}}>
        <Card.Content style = {{ alignContent :'center'}}>
        <Subheading>Wind Stats in {this.state.weatherobject.name}:</Subheading>
        <Paragraph> Direction (Wind Degree) : {this.state.wind_degree}{'\u00b0'}'s</Paragraph>
        <Paragraph> Wind Speed : {this.state.wind_speed}</Paragraph>

      </Card.Content>
      
      
      </Card>
      <Card style = {{borderColor:'black',borderRadius : 10,margin:4,borderBottomWidth:2}}>
        <Card.Content style = {{ alignContent :'center'}}>
        <Subheading>Co-ordinates of {this.state.weatherobject.name}:</Subheading>
        <Paragraph> Latitude : {this.state.lat}</Paragraph>
        <Paragraph> Longitude : {this.state.long}
</Paragraph>

      </Card.Content>    
      </Card>

      <Card  style = {{borderColor:'black',borderRadius : 10,margin:4,borderBottomWidth:2}}>
      <Card.Content>
        <Subheading>
          Today's Picture
        </Subheading>
        <Card.Cover source={{ uri: `https://i.picsum.photos/id/${Math.floor(Math.random() * 1000) + 1  }/700/700.jpg` }}  style = {{marginTop:10}}/> 
      </Card.Content>
         
      </Card>
    {mylike}
    
    </Card.Content>
    
    
        </Card>
        </ScrollView>
        </View>

    );
  }
  else {
    return(
    <Loading />
    )
  }
  }
}
var styles = StyleSheet.create({
    cardView : {
        borderRadius:40,
        margin:30,
        borderWidth:3,
        borderColor : 'black',
        backgroundColor:'#F5F5F5'
    }
})
